import React, { useState,useEffect } from 'react'
import {useParams} from "react-router-dom";
import { app } from "../firebase";

const db = app.firestore();



const  Post=()=> {
    let { id } = useParams();
    let post

    useEffect(()=>{

        db.collection('posts')
        .doc(id)
        .get()
        .then(doc=>{
            post=doc
        })
    }

    )
    return(
    <h1>{post.title}</h1>
    )
}

export default Post;
