import React, { useState,useEffect } from 'react'
import {useParams} from "react-router-dom";
import { app } from "../firebase";

const db = app.firestore();



const  Post=()=> {
    let { id } = useParams();
    let [post,setPost]=useState("")
    console.log(id)
    useEffect(()=>{
        db.collection('posts')
        .doc(id)
        .get()
        .then(doc=>{
            post=doc.data()
            setPost(post)
        })
    },[id])


    console.log(post)
    return(
    <h1>{post.title}</h1>
    )
}

export default Post;
