import React, { useState,useEffect } from 'react'
import {useParams} from "react-router-dom";
import { app } from "../firebase";

const db = app.firestore();



const  Post=()=> {
    let { id } = useParams();
    const [post,setPost]=useState("")

    useEffect(()=>{
        db.collection('posts')
        .doc(id)
        .get()
        .then(doc=>{
            setPost(post=doc)
        })
    }

    )
    return(
    <h1>{post.title}</h1>
    )
}

export default Post;
