import React from 'react'
import {useParams} from "react-router-dom";
import { app } from "../firebase";

const db = app.firestore();

const  Post=()=> {
    let { id } = useParams();
    console.log(id)
    db.collection('posts')
    .doc(id)
    .onSnapshot()
    .then(doc=>{
        console.log(doc.data())
    }).catch(err=>console.log(err))
    return(
    <h1>Hello</h1>
    )
}

export default Post;
