    <div className="feeds">
        <ul>{
           posts.map(post=>(
          <li>
          <SinglePost key={post.emailId}  title={post.title} content={post.content} imageUrl={post.imageUrl} likesCount={post.likesCount}/>
        </li>
        ))          
          }
        </ul>
      </div>
     );
}

export default YourStories;
