import {
  Nav,
  Navbar,
  FormControl,
  Button,
  Form,
  Dropdown,
  DropdownButton,
} from "react-bootstrap";
import React,{useContext} from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.min.js";
import SignIn from './SignIn';
import {AuthContext} from "../Auth";
import firebase from 'firebase'
const NavBar = () => {
  const {authuser}=useContext(AuthContext) 
  let buttonTag= authuser ? "Log Out" : "Sign In"

  return ( <div>
    <Navbar bg="light" variant="light">
      <Navbar.Brand href="/">Medium Clone</Navbar.Brand>
      <Nav className="mr-auto">
        <Nav.Link href="/">Home</Nav.Link>
        <Nav.Link href="#features">Popular</Nav.Link>
        <Nav.Link href="#pricing">Latest</Nav.Link>
      </Nav>
      {/* <Form inline>
        <FormControl type="text" placeholder="Search" className="mr-sm-2" />
        <Button variant="outline-primary">Search</Button>
      </Form> */}
      <Button style={{marginRight: '11px'}} onClick={()=>{
        if(authuser){
          firebase.auth().signOut()
        }
        else{
          window.location.href = "/signIn";
        }
      }} variant={authuser ? "danger" :"Warning"}>{buttonTag}</Button>
      <DropdownButton 
      eventKey={3} 
      title={
        <span><i className="fa fa-user fa-fw"></i> Bootstrap</span>
      }
    >
      <MenuItem eventKey='1'><i className="fa fa-envelope fa-fw"></i> User Profile</MenuItem>
      <MenuItem eventKey='2'><i className="fa fa-gear fa-fw"></i> Settings</MenuItem>
      <MenuItem divider />
      <MenuItem eventKey='3'><i className="fa fa-sign-out fa-fw"></i> Logout</MenuItem>
    </DropdownButton>

      {/* <DropdownButton
        id="dropdown-item-button"
        title={buttonTag}
      >
        <Dropdown.Item as="button">
          <a href="/addPost" style={{ textDecoration: "none" }}>
            New Story
          </a>
        </Dropdown.Item>
        <Dropdown.Item as="button">
          <a href="/yourStories" style={{ textDecoration: "none" }}>
            Your Stories
          </a>
        </Dropdown.Item>
      </DropdownButton> */}
    </Navbar>
  </div> );
}

export default NavBar;
